from flask import Flask, render_template, requestfrom flask_sqlalchemy import SQLAlchemyfrom datetime import datetimeapp = Flask(__name__)app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///blog.db'db = SQLAlchemy(app)# Static dataposts = [    {        'id': 1,        'author': 'sagor',        'title': 'How to install flask on mac OS',        'content': 'You may use a command link like, pip install flask or sudo pip install flask',        'date': 'April, 07, 2020',    },    {        'id': 2,        'author': 'shimul',        'title': 'How to install django on mac OS',        'content': 'You may use a command link like, pip install django or sudo pip install django',        'date': 'April, 08, 2020',    }]class BlogPost(db.Model):    id = db.Column(db.Integer, primary_key=True)    author = db.Column(db.String(50), unique=True, nullable=False)    title = db.Column(db.String(70), nullable=False)    content = db.Column(db.Text, nullable=False)    created_at = db.Column(db.DateTime, nullable=False,                           default=datetime.utcnow)    def __repr__(self):        return '<Title %r>' % self.title# @app.route('/', methods=['get', 'post'])@app.route('/')def homepage():    return render_template('index.html', posts=posts)@app.route('/about')def about_us():    return render_template('about.html')if __name__ == '__main__':    app.run(debug=True)