from flask_wtf import FlaskFormfrom wtforms import StringField, PasswordField, SubmitField, BooleanFieldfrom wtforms.validators import DataRequired, length, Email, EqualToclass RegistrationForm(FlaskForm):    username = StringField('Username', validators=[DataRequired(), length(min=2, max=20)])    email = StringField('Email', validators=[DataRequired(), Email()])    password = PasswordField('Password', validators=[DataRequired()])    confirm_password = PasswordField('Confirm Password', validators=[DataRequired(), EqualTo('password')])    sing_up = SubmitField('Sing-up')class LoginForm(FlaskForm):    email = StringField('Email', validators=[DataRequired(), Email()])    password = PasswordField('Password', validators=[DataRequired()])    remember = BooleanField('Remember me')    sing_in = SubmitField('Sing-in')